import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux'
import './App.css';
import { Input, Button, Card, Divider, Row } from 'antd';
import { createRoom, joinRoom, setUsername } from './utils';

const lightWhite = '#ffffff38'

const UserName = () => {
  const [ usernameInput, setUsernameInput ] = useState( "" );
  const dispatch = useDispatch();
  const enterRooom = () => dispatch( setUsername( usernameInput ) );

  return (
    <div className="user">
      <input type="text" placeholder="Enter username" value={usernameInput} onChange={( e ) => setUsernameInput( e.target.value )} />
      <button onClick={enterRooom}>Enter Room</button>
    </div>
  )
}

const SignIn = () => {
  const [ roomName, setRoomName ] = useState( "" );
  const [ roomId, setRoomId ] = useState( "" );
  const room = useSelector( state => state.room );
  console.log( room )
  const dispatch = useDispatch();

  return (
    <Row justify={'center'}>
      <Card style={{ width: 550, background: lightWhite }}>
        <Row>
          <Input
            size="large"
            placeholder="Create a new room"
            onChange={( e ) => setRoomName( e.target.value )}
            onPressEnter={() => dispatch( createRoom( roomName ) )}
            style={{ maxWidth: 400, minWidth: 200 }}
          />
          <Button type='primary' onClick={() => dispatch( createRoom( roomName ) )} style={{ height: 40, width: 75 }}>
            Create
            </Button>
        </Row>
        <Divider style={{ background: lightWhite }} />
        <Row>
          <Input
            size="large"
            placeholder="Join an existing room"
            onChange={( e ) => setRoomId( e.target.value )}
            onPressEnter={() => dispatch( joinRoom( roomId ) )}
            style={{ maxWidth: 400, minWidth: 200 }}
          />
          <Button type='primary' onClick={() => dispatch( joinRoom( roomId ) )} style={{ height: 40, width: 75 }}>
            Join
            </Button>
        </Row>
      </Card>
      <UserName />
    </Row>
  );
}

export default SignIn;
