import React, { useState } from 'react';
import { Provider, useSelector, useDispatch } from 'react-redux'
import './App.css';
import { Input, Typography, Button, Card, Divider, Row } from 'antd';
import WebSocketProvider from './utils/WebSocket';
import store from './utils/store';
import { createRoom, joinRoom } from './utils/actions';
import Chat from './Room'
const { Text } = Typography;

const SignIn = () => {
  const [ roomName, setRoomName ] = useState( "" );
  const [ roomId, setRoomId ] = useState( "" );
  const currentRoom = useSelector( state => state.room );

  const dispatch = useDispatch();

  return (
    <>
      {!currentRoom &&
        <Card style={{ width: 550 }}>
          <Text>Create a new room</Text>
          <Row>
            <Input
              size="large"
              placeholder="Room name"
              onChange={( e ) => setRoomName( e.target.value )}
              onEnter={() => dispatch( createRoom( roomName ) )}
              style={{ maxWidth: 400, minWidth: 200 }}
            />
            <Button type='primary' onClick={() => dispatch( createRoom( roomName ) )} style={{ height: 40, width: 75 }}>
              Create
            </Button>
          </Row>
          <Divider />

          <Text>Join an existing room</Text>
          <Row>
            <Input
              size="large"
              placeholder="Room code"
              onChange={( e ) => setRoomId( e.target.value )}
              onEnter={() => dispatch( joinRoom( roomId ) )}
              style={{ maxWidth: 400, minWidth: 200 }}
            />
            <Button type='primary' onClick={() => dispatch( joinRoom( roomId ) )} style={{ height: 40, width: 75 }}>
              Join
            </Button>
          </Row>
        </Card>
      }

      {currentRoom && <Chat />}
    </>
  );
}

function Home() {
  return (
    <Provider store={store}>
      <WebSocketProvider>
        <SignIn />
      </WebSocketProvider>
    </Provider>
  )
}

export default Home;
