import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux'
import './App.css';
import { Input, Button, Card, Divider, Row, Radio, Typography } from 'antd';
import { createRoom, joinRoom, setUsername } from './utils';
import { icons } from './icons/svg';
const { Text } = Typography
const lightWhite = '#ffffff38'
const btnStyle = { height: 40, width: 75 }
const inputStyle = { maxWidth: 400, minWidth: 200 }
const iconStyle = { height: 'auto', borderRadius: 6, padding: 0, margin: 0 }

const randomIcon = () => Object.keys( icons )[ Math.floor( Math.random() * 11 ) ]

const CreateUserName = () => {
  const [ usernameInput, setUsernameInput ] = useState( "" );
  const [ icon, setIcon ] = useState( randomIcon() )

  const dispatch = useDispatch();
  const enterRoom = () => dispatch( setUsername( usernameInput ) );

  const iconButton = ( item ) => (
    <button
      value={item}
      key={item}
      style={{
        ...iconStyle,
        background: icon === item ? lightWhite : 'none',
        border: icon === item ? '2px solid white' : 'none'
      }}
      onClick={() => setIcon( item )}
    >
      {icons[ item ]}
    </button>
  )

  return (
    <>
      <Row style={{ minHeight: 70 }}>
        <Text>Select avatar:</Text>
        <Radio.Group style={{ display: 'flex', marginTop: 3 }}>
          {Object.keys( icons ).map( item => iconButton( item ) )}
        </Radio.Group>
      </Row>
      <Divider style={{ background: lightWhite }} />
      <Row>
        <Input
          size="large"
          placeholder="Enter username"
          onChange={( e ) => setUsernameInput( e.target.value )}
          onPressEnter={enterRoom}
          style={inputStyle}
        />
        <Button type='primary' onClick={enterRoom} style={btnStyle}>Join</Button>
      </Row>
    </>
  )
}

const CreateOrJoinRoom = () => {
  const [ roomName, setRoomName ] = useState( "" );
  const [ roomId, setRoomId ] = useState( "" );
  const dispatch = useDispatch();

  return (
    <>
      <Row>
        <Input
          size="large"
          placeholder="Create a new room"
          onChange={( e ) => setRoomName( e.target.value )}
          onPressEnter={() => dispatch( createRoom( roomName ) )}
          style={inputStyle}
        />
        <Button type='primary' onClick={() => dispatch( createRoom( roomName ) )} style={btnStyle}>
          Create
        </Button>
      </Row>
      <Divider style={{ background: lightWhite }} />
      <Row>
        <Input
          size="large"
          placeholder="Join an existing room"
          onChange={( e ) => setRoomId( e.target.value )}
          onPressEnter={() => dispatch( joinRoom( roomId ) )}
          style={inputStyle}
        />
        <Button type='primary' onClick={() => dispatch( joinRoom( roomId ) )} style={btnStyle}>
          Join
        </Button>
      </Row>
    </>
  )
}

const SignIn = () => {
  const room = useSelector( state => state.room );
  return (
    <Row justify={'center'}>
      <Card style={{ width: 550, background: lightWhite }}>
        {room ? <CreateOrJoinRoom /> : <CreateUserName />}
      </Card>
    </Row>
  );
}

export default SignIn;
