import React, { useState } from 'react';
import { Provider, useSelector, useDispatch } from 'react-redux'
import './App.css';
import { Input, Typography, Button } from 'antd';
import WebSocketProvider from './utils/WebSocket';
import store from './utils/store';
import { createRoom, joinRoom } from './utils/actions';
import Chat from './Room'
const { Text } = Typography;

const SignIn = () => {
  const [ roomName, setRoomName ] = useState( "" );
  const [ roomId, setRoomId ] = useState( "" );
  const currentRoom = useSelector( state => state.room );

  const dispatch = useDispatch();

  return (
    <>
      {!currentRoom &&
        <div className="create">
          <div>
            <Text>Create a new room</Text>
            <Input size="large" placeholder="Room name" onChange={( e ) => setRoomName( e.target.value )} />
            <Button>Create</Button>


            <Text>Join an existing room</Text>
            <Input size="large" placeholder="Room code" />

            <span>Create new room</span>
            <input type="text" placeholder="Room name" value={roomName} onChange={( e ) => setRoomName( e.target.value )} />
            <button onClick={() => dispatch( createRoom( roomName ) )}>Create</button>
          </div>
          <div>



            <span>Join existing room</span>
            <input type="text" placeholder="Room code" value={roomId} onChange={( e ) => setRoomId( e.target.value )} />
            <button onClick={() => dispatch( joinRoom( roomId ) )}>Join</button>
          </div>
        </div>
      }

      {currentRoom && <Chat />}
    </>
  );
}

function Home() {
  return (
    <Provider store={store}>
      <WebSocketProvider>
        <SignIn />
      </WebSocketProvider>
    </Provider>
  )
}

export default Home;
