import React from 'react';
import { Provider, useSelector } from 'react-redux';
import { Layout, Typography, } from 'antd';
import SignIn from './SignIn'
import Chat from './Room'
import './App.css';
import { WebSocketProvider, store } from './utils';
const { Header } = Layout;
const { Title } = Typography;

const App = () => {
  const currentRoom = useSelector( state => state.room );
  if ( !currentRoom ) return <SignIn />
  return (
    <Layout style={{ background: 'none' }}>
      <Header style={{ background: 'none', marginTop: 30 }}>
        <Title level={1} style={{ color: 'white', textAlign: 'center', maxWidth: 1300 }}>GAME NIGHT</Title>
      </Header>
      <Chat />
    </Layout>
  )
}

const AppWrapper = () => {
  return (
    <Provider store={store}>
      <WebSocketProvider>
        <App />
      </WebSocketProvider>
    </Provider>
  )
}
export default AppWrapper
